instructions = input.split('')

floor = 0
basement = 0
instructions.each_with_index do |i, idx|
  if i == "("
    floor += 1
  elsif i == ")"
    floor -= 1
  end

  if floor < 0 and basement == 0
    basement = idx + 1
  end
end

puts "You end up on floor #{floor}"
puts "You enter the basement at instruction #{basement}"
